import pandas as pdimport numpy as npimport matplotlib.pyplot as plt#reading data setdf= pd.read_csv("Social_Network_Ads.csv")x = df.iloc[:,0:2].values#visualisation of dataplt.scatter(x[:,0],x[:,1])plt.title("K-mean visualisation")plt.xlabel("Annual income")plt.ylabel("Spending score")plt.show()#apply k-mean clusturingfrom sklearn.cluster import KMeans#HOW TO CHOOSE NUMBER OF Kwcss = []for i in range(1,11):    km=KMeans(n_clusters=i)     km.fit(x)    a = km.inertia_    wcss.append(a)plt.plot(range(1,11),wcss)plt.title("Elbow method")plt.xlabel("number of K")plt.ylabel("WCSS")plt.show()#After Elbow method we found best number of Ki 5from sklearn.cluster import KMeanskm=KMeans(n_clusters=5) km.fit(x)#predictiony = km.predict(x)set(y)#Visualization of clusterd dataplt.scatter(x[y==0,0],x[y==0,1],color="red",label="1st Cluster")plt.scatter(x[y==1,0],x[y==1,1],color="blue",label="2nd Cluster")plt.scatter(x[y==2,0],x[y==2,1],color="green",label="3rd Cluster")plt.scatter(x[y==3,0],x[y==3,1],color="yellow",label="4th Cluster")plt.scatter(x[y==4,0],x[y==4,1],color="orange",label="5th Cluster")plt.scatter(km.cluster_centers_[:,0],km.cluster_centers_[:,1],color="black",            label="Centeroid",s=100)plt.title("K-mean visualisation")plt.xlabel("Annual income")plt.ylabel("Spending score")plt.legend()plt.show()# applying KNN algorithmfrom sklearn.neighbors import KNeighborsClassifier  classifier= KNeighborsClassifier(n_neighbors=5, metric='minkowski', p=2 )  classifier.fit(x,y)y1=classifier.predict(x)from sklearn.metrics import accuracy_scoreaccuracy_score(y1,y)#visualisation of KNN data